import React from 'react';
//import logo from './logo.svg';
import './App.css';
import { BrowserRouter, Route, Switch } from "react-router-dom";
import CreateRoom from "./routes/CreateRoom";
import Room from "./routes/Room";
import SignIn from "./routes/SignIn/SignIn";
import SignUp from "./routes/SignUp/SignUp";
import Home from "./routes/Home/Home";
import {Component} from 'react';

class App extends Component {
  render() {

    const HomeView = () => (
      <React.Fragment>
        {localStorage.getItem('isLoggedIn') ? <Home/>: <SignIn />}
      </React.Fragment>
    );

    const CreateRoomView = () => (
      <React.Fragment>
        {localStorage.getItem('isLoggedIn') ? <CreateRoom /> : <SignIn />}
      </React.Fragment>
    );

    const RoomView = () => (
      <React.Fragment>
        {localStorage.getItem('isLoggedIn') ? <Room /> : <SignIn />}
      </React.Fragment>
    );

    const SignInView = () => (
      <React.Fragment>
        {localStorage.getItem('isLoggedIn') ?<HomeView /> : <SignIn />}
      </React.Fragment>
    );

    const SignUpView = () => (
      <React.Fragment>
        {localStorage.getItem('isLoggedIn') ? <HomeView /> : <SignUp />}
      </React.Fragment>
    );

    return (
    <div className="App">
      <BrowserRouter>
        <Switch>
          <Route path="/" exact component={SignInView}/>
          <Route path="/signin" exact component={SignInView}/>
          <Route path="/signup" exact component={SignUpView}/>
          <Route path="/home" exact component={HomeView} />
          <Route path="/room" component={CreateRoomView} />
          <Route path="/room/:roomID" component={RoomView} />
        </Switch>
      </BrowserRouter>
    </div>);
  }
}

export default App;
