export function validateEmail(email){
    if(email===""){
        throw new Exception("ExceptionEmail","Empty email");
    }
    else if (!(/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/.test(email))){
        throw new Exception("ExceptionEmail","Invalid email");
    }
    return true;
}

export function validatePassword(password){
    if(password===""){
        throw new Exception("ExceptionPassword","Empty password");
    }
    return true;
}

export function validateUsername(username){
    if(username===""){
        throw new Exception("ExceptionUsername", "Empty username");
    }
    return true;
}

